package octopus.utils;import java.lang.annotation.Documented;import java.lang.annotation.ElementType;import java.lang.annotation.Inherited;import java.lang.annotation.Retention;import java.lang.annotation.RetentionPolicy;import java.lang.annotation.Target;/*** * This annotation is for tell the data source for a method, or a field.  * The data source is a block area in a spread sheet file. To make correctly * read the col and row value, please focus the upper left data cell and read the * x or y value -1. * @author yangdo */@Target({ ElementType.FIELD, ElementType.METHOD, ElementType.PARAMETER })@Retention(RetentionPolicy.RUNTIME)@Documented@Inheritedpublic @interface DataSource {	public enum BatchMode {		horizontal, vertical, verticalStandalone	};	String sheet();	int col() default 0;	int row() default 0;	BatchMode mode() default BatchMode.horizontal;	String keyField() default "key";}